import React from "react";
import Home from "./pages/Home";
import Details from "./pages/Details";
import Header from "./components/Header";
import { BrowserRouter, Route, Routes } from "react-router-dom";


function App() {

  return (
    // In order to use the concept of Routing we wrap the components inside the BrowserRouter component, which we get from "react-router-dom" module. 
    <BrowserRouter>

      <div className="App">
        <Header />
      </div>

      {/* Defining the Link tag, we don't use the <a></a> here because if we do. Then the whole page will get re-rendered everytime the user clicks on a different page.. */}

      <Routes>
        <Route path="/" element={ <Home /> } />
        <Route path="details" element={ <Details /> } /> 
      </Routes>

    </BrowserRouter>
  );
}

export default App;
