import React from "react";
import { useState, useEffect } from "react";
import { DataGrid } from '@mui/x-data-grid';
import Button from '@mui/material/Button';
import { GridColDef, GridApi, GridCellValue } from '@mui/x-data-grid';
import DataModal from "./DataModal"


function TableMUI() {

    const [selectedData, setSelectedData] = useState({})

    // defining the column section of the table 
    const columns = [
        {field: "id", headerName: "ID"},
        {field: "name", headerName: "Name", width: 200},
        {field: "username", headerName: "User Name", width: 200},
        {field: "email", headerName: "Email", width: 300},
        {field: "phone", headerName: "Phone", width: 250},
        {field: "website", headerName: "Website", width: 200},
    
        {
        field: 'action',
        headerName: 'Action',
        sortable: false,
        renderCell: (params) => {
          const onClick = (x) => {
              console.log("x",x)
            x.stopPropagation(); // don't select this row after clicking
    
            const api = params.api;
            const thisRow = {};
    
            api
              .getAllColumns()
              .filter((c) => c.field !== '__check__' && !!c)
              .forEach((c) => (thisRow[c.field] = params.getValue(params.id, c.field)));
                setSelectedData(thisRow)
              };
              
                // return <Button variant="outlined" color="secondary" onClick={(event) => handleClick(event.target.value)} >Details</Button>;
                // return <Button variant="outlined" color="secondary" onClick={onClick} >Details</Button>;
                return <div onClick={onClick}><DataModal  selectedData={selectedData} variant="outlined" color="secondary"/></div>
                },
            },        
    ]


    // fetching data from the API and setting it to tableData to be shown in the row section of the table
    const [tableData, setTableData] = useState([])

    useEffect(() => {
        fetch("https://jsonplaceholder.typicode.com/users")
        .then(response => response.json())
        .then(data => setTableData(data))
        .catch(error => console.log("Error =>", error)); 
    }, [])

    
    return(

        <div style={{height: 650, width: '90%', textAlign: "center"}}>

            <DataGrid 
                rows={tableData}
                columns={columns}
                // pageSize={10}
                initialState={{
                    sorting: {
                        sortModel: [{ field: '', sort: 'asc' }],
                    },
                    filter: {
                        filterModel: {
                            items: [{ columnField: '', operatorValue: '>', value: '' }],
                        },
                    },
                }}

            />
            <DataModal selectedData={selectedData}/>

        </div>
    );
}

export default TableMUI;
