import React from "react";
import { useState, useEffect } from "react";
import Details from "./Details";
import { DataGrid } from '@mui/x-data-grid';
import Button from '@mui/material/Button';
import { GridColDef, GridApi, GridCellValue } from '@mui/x-data-grid';


const columns = [
    {field: "id", headerName: "ID"},
    {field: "name", headerName: "Name", width: 200},
    {field: "username", headerName: "User Name", width: 200},
    {field: "email", headerName: "Email", width: 300},
    {field: "phone", headerName: "Phone", width: 250},
    {field: "website", headerName: "Website", width: 200},
    // {field: "action", headerName: "Action",  width: 150},
    // {
    //     field: "action",
    //     headerName: "Action",
    //     sortable: false,
    //     renderCell: (params) => {

    //         const onClick = (e) => {
    //             e.stopPropagation();

    //             const api = grid: gridApi 
    //         }
    //     //   return <Button variant="outlined" color="secondary"
    //     //     onClick={() => 
    //     //         {
    //     //             alert("clicked..");

    //     //         }}>Details</Button>;
    //     }
    //   },

    // ---------------------------------------------------

    {
    field: 'action',
    headerName: 'Action',
    sortable: false,
    renderCell: (params) => {
      const onClick = (e) => {
        e.stopPropagation(); // don't select this row after clicking

        const api: GridApi = params.api;
        const thisRow: Record<string, GridCellValue> = {};

        api
          .getAllColumns()
          .filter((c) => c.field !== '__check__' && !!c)
          .forEach(
            (c) => (thisRow[c.field] = params.getValue(params.id, c.field)),
          );

        return alert(JSON.stringify(thisRow, null, 4));
      };

      return <Button onClick={onClick}>Click</Button>;
    },
  },
  { field: 'id', headerName: 'ID', width: 70 },
  { field: 'firstName', headerName: 'First name', width: 130 },
  { field: 'lastName', headerName: 'Last name', width: 130 },
  {
    field: 'age',
    headerName: 'Age',
    type: 'number',
    width: 90,
  },
  {
    field: 'fullName',
    headerName: 'Full name',
    description: 'This column has a value getter and is not sortable.',
    sortable: false,
    width: 160,
    valueGetter: (params) =>
      `${params.getValue(params.id, 'firstName') || ''} ${
        params.getValue(params.id, 'lastName') || ''
      }`,
  },


    // ---------------------------------------------------

]





function TableMUI() {

    const [tableData, setTableData] = useState([])

    useEffect(() => {
        fetch("https://jsonplaceholder.typicode.com/users")
        .then(response => response.json())
        .then(data => setTableData(data))
        .catch(error => console.log("Error =>", error)); 
    }, [])

    return(

        <div style={{height: 650, width: '90%', textAlign: "center"}}>

            <DataGrid 
                rows={tableData}
                columns={columns}
                // pageSize={10}
                initialState={{
                    sorting: {
                        sortModel: [{ field: '', sort: 'asc' }],
                    },
                    filter: {
                        filterModel: {
                            items: [{ columnField: '', operatorValue: '>', value: '' }],
                        },
                    },
                }}
                // checkboxSelection
            />

        </div>
    );
}

export default TableMUI;
