import React from "react";
import { useState, useEffect } from "react";
import { DataGrid } from '@mui/x-data-grid';
import Button from '@mui/material/Button';
import { GridColDef, GridApi, GridCellValue } from '@mui/x-data-grid';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';


// defining the column section of the table 
const columns = [
    {field: "id", headerName: "ID"},
    {field: "name", headerName: "Name", width: 200},
    {field: "username", headerName: "User Name", width: 200},
    {field: "email", headerName: "Email", width: 300},
    {field: "phone", headerName: "Phone", width: 250},
    {field: "website", headerName: "Website", width: 200},

    {
    field: 'action',
    headerName: 'Action',
    sortable: false,
    renderCell: (params) => {
      const onClick = (x) => {
        x.stopPropagation(); // don't select this row after clicking

        const api: GridApi = params.api;
        const thisRow: Record <string, GridCellValue> = {};

        const style = {
            position: 'absolute',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            width: 400,
            bgcolor: 'background.paper',
            border: '2px solid #000',
            boxShadow: 24,
            p: 4,
          };

            // const [open, setOpen] = React.useState(false);
            // const handleOpen = () => setOpen(true);
            // const handleClose = () => setOpen(false);

        api
          .getAllColumns()
          .filter((c) => c.field !== '__check__' && !!c)
          .forEach((c) => (thisRow[c.field] = params.getValue(params.id, c.field)));
            
          return alert(JSON.stringify(thisRow, null, 4));
          };
              

                return (
                  <div>
                    <Button onClick={onClick}>Open modal</Button>
                    <Modal
                      open={x}
                      onClose={onClick}
                      aria-labelledby="modal-modal-title"
                      aria-describedby="modal-modal-description"
                    >
                      <Box sx={style}>
                        <Typography id="modal-modal-title" variant="h6" component="h2">
                          Text in a modal
                        </Typography>
                        <Typography id="modal-modal-description" sx={{ mt: 2 }}>
                          Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
                        </Typography>
                      </Box>
                    </Modal>
                  </div>
                );
              
              
            
            // <Button variant="outlined" color="secondary" onClick={onClick} >Details</Button>;
            },
        },        
]


function TableMUI() {

    // fetching data from the API and setting it to tableData to be shown in the row section of the table
    const [tableData, setTableData] = useState([])

    useEffect(() => {
        fetch("https://jsonplaceholder.typicode.com/users")
        .then(response => response.json())
        .then(data => setTableData(data))
        .catch(error => console.log("Error =>", error)); 
    }, [])

    return(

        <div style={{height: 650, width: '90%', textAlign: "center"}}>

            <DataGrid 
                rows={tableData}
                columns={columns}
                // pageSize={10}
                initialState={{
                    sorting: {
                        sortModel: [{ field: '', sort: 'asc' }],
                    },
                    filter: {
                        filterModel: {
                            items: [{ columnField: '', operatorValue: '>', value: '' }],
                        },
                    },
                }}
            />

        </div>
    );
}

export default TableMUI;
