import React from "react";
import { useState, useEffect } from "react";
import { DataGrid } from '@mui/x-data-grid';
import { buttonBaseClasses } from "@mui/material";
import { textAlign } from "@mui/system";

const columns = [
    {field: "id", headerName: "ID"},
    {field: "name", headerName: "Name", width: 200},
    {field: "username", headerName: "User Name", width: 200},
    {field: "email", headerName: "Email", width: 300},
    {field: "phone", headerName: "Phone", width: 250},
    {field: "website", headerName: "Website", width: 200},
    // {field: " ", headerName: "Action"},
]

// const customColumn = [
//     {
//         headerName: "Action",
//         customField: { 
//             ID: "id", 
//             Name: "name", 
//             userName: "username", 
//             Email: "email", 
//             Phone: "phone", 
//             Website: "website"
//         }
//     }
// ]

// let newColumn = columns.push(customColumn);


function TableMUI() {

    const [tableData, setTableData] = useState([])

    useEffect(() => {
        
        fetch("https://jsonplaceholder.typicode.com/users")
        // fetch("https://jsonplaceholder.typicode.com/todos")
        .then(response => response.json())
        .then(data => setTableData(data))
        .catch(error => console.log("Error =>", error)); 

    }, [])


    return(

        <div style={{height: 650, width: '90%', textAlign: "center"}}>

            <DataGrid 
                rows={tableData}
                columns={{columns}}
                // pageSize={10}
                initialState={{
                    sorting: {
                        sortModel: [{ field: '', sort: 'asc' }],
                    },

                    filter: {
                        filterModel: {
                            items: [{ columnField: '', operatorValue: '>', value: '' }],
                        },
                    },
                }}

                            columns={[{ field: 'Action' }]}
                            rows={[
                              {
                                id: 11,
                                Action: '<button>details</button>',
                              },
                             ]}
                // newColumn={newColumn}
                // checkboxSelection
            />

            <DataGrid
                columns={[{ field: 'username' }, { field: 'age' }]}
                rows={[
                    {
                        id: 1,
                        username: '@MUI',
                        age: 20,
                    },
                ]}
             />

        </div>
    );
}

export default TableMUI;
